import './App.css';
import React, {useEffect, useState} from "react";
import {Routes, Route} from 'react-router-dom'
import Recipes from './/components/Recipes'
import Recipe from "./components/Recipe";
import axios from "axios";
import Header from "./components/Header";


const API_RECIPES = `http://127.0.0.1:8000/api/recipes`
const API_CATEGORIES = "http://127.0.0.1:8000/api/category"

function App(){
    const [recipes, setRecipes] = useState([])
    const [categories, setCategories] = useState([])
    const [categoryId, setCategoryId] = useState(0)

    useEffect(() => {
        axios.get(API_RECIPES + `?${categoryId ? `category=${categoryId}` : ''}`)
            .then(rec => {
                setRecipes(rec.data)
            })
        axios.get(API_CATEGORIES)
            .then(cat => {
                setCategories(cat.data)
            })
    }, [categoryId]);

    return (
        <div className='App'>
            <Header/>
            <div className='categories'>
                <div
                    onClick={() => setCategoryId(0)}
                    className={categoryId === 0 ? 'active' : ''}>
                    Все
                </div>
                {categories.map(el =>(
                    <div
                        onClick={() => setCategoryId(el.title)}
                        className={categoryId === el.title ? 'active' : ''}
                        key={el.id}>
                        {el.title}
                    </div>
                ))}
            </div>

            {recipes.map(el => (
                <Routes>
                    <Route path='/' element={
                        <Recipes key={el.id} title={el.title}/>
                    } />
                    <Route path='/recipe' element={
                        <Recipe key={el.id} title={el.title} text={el.title}/>
                    } />
                </Routes>
            ))}
        </div>
    );
}


export default App;
