import {useEffect, useState} from "react";
import {Container, Table} from "reactstrap";
import axios from "axios";
import {API_URL_RECIPES} from "../../index";
import Categories, {chooseCategory} from "../appCategories/Categories";


const Home = () => {
    const [recipes, setRecipes] = useState([])

    useEffect(() => {
        getRecipes()
    }, []);

    const getRecipes = (data)=>{
        axios.get(API_URL_RECIPES).then(data => setRecipes(data.data))
    }

    return(
        <Container>
            <Table>
                <thead>
                <tr>
                    <th>Название</th>
                    <th>Текст</th>
                    <th>Категория</th>
                    <th>{chooseCategory}</th>
                </tr>
                </thead>
                <tbody>
                {recipes.map(recipe => (
                        <tr key={recipe.id}>
                            <td>{recipe.title}</td>
                            <td>{recipe.text}</td>
                            <td>{recipe.category}</td>
                        </tr>
                    )
                )}
                </tbody>
            </Table>
        </Container>
    )
}

export default Home;
