import React, {useEffect, useState} from "react";
import {Container, Table} from "reactstrap";
import axios from "axios";
import './Home.css';


const API_RECIPES = "http://127.0.0.1:8000/api/recipes"
const API_CATEGORIES = "http://127.0.0.1:8000/api/category"

const Home = () => {
    const [recipes, setRecipes] = useState([])
    const [categories, setCategories] = useState([])

    useEffect(() => {
        axios.get(API_RECIPES)
            .then(rec => {
                setRecipes(rec.data)
                console.log(rec.data)
            })
        axios.get(API_CATEGORIES)
            .then(cat => {
                setCategories(cat.data)
                console.log(cat.data)
            })
    }, []);

    function chooseCategory(category){
        console.log(category)
    }

    return(
        <Container>
            <div className='categories'>
                {categories.map(category =>(
                    <div key={category.id} onClick={() => chooseCategory(category.title)}>{category.title}</div>
                ))}
            </div>
            <Table>
                <thead>
                <tr>
                    <th>Название</th>
                    <th>Текст</th>
                    <th>Категория</th>
                </tr>
                </thead>
                <tbody>
                {recipes.map(recipe => (
                        <tr key={recipe.id}>
                            <td>{recipe.title}</td>
                            <td>{recipe.text}</td>
                            <td>{recipe.category}</td>
                        </tr>
                    )
                )}
                </tbody>
            </Table>
        </Container>
    )
}




export default Home;
