import './App.css';
import React, {useEffect, useState} from "react";
import Recipes from './/components/Recipes'
import axios from "axios";


const API_RECIPES = `http://127.0.0.1:8000/api/recipes`
const API_CATEGORIES = "http://127.0.0.1:8000/api/category"

function App(){
    const [recipes, setRecipes] = useState([])
    const [categories, setCategories] = useState([])
    const [categoryId, setCategoryId] = useState(0)

    useEffect(() => {
        axios.get(API_RECIPES + `${categoryID}`)
            .then(rec => {
                setRecipes(rec.data)
            })
        axios.get(API_CATEGORIES)
            .then(cat => {
                setCategories(cat.data)
            })
    }, []);

    function chooseCategory(category){
        console.log(category)
    }

    return (
        <div className='App'>
            <div className="text-center">
                <div>
                    <h1 className='logo'>Рецепты</h1>
                    <ul className='nav'>
                    </ul>
                </div>
            </div>
            <div className='categories'>
                <div
                    onClick={() => setCategoryId(0)}
                    className={categoryId === 0 ? 'active' : ''}>
                    Все
                </div>
                {categories.map(el =>(
                    <div
                        onClick={() => setCategoryId(el.id)}
                        className={categoryId === el.id ? 'active' : ''}
                        key={el.id}>
                        {el.title}
                    </div>
                ))}
            </div>
            {recipes.map(el => (
                <Recipes key={el.id}
                    title={el.title}
                    text={el.text}
                />
            ))}
        </div>
    );
}


export default App;
