import React, {useEffect, useState} from "react";
import {API_URL_CATEGORIES} from "../../index";
import axios from "axios";
import './Categories.css'
import {chooseCategory} from "../appHome/Home";

const Categories = () => {
    const [categories, setCategories] = useState([])

    useEffect(() => {
        getCategories()
    }, []);

    const getCategories = (date) =>{
        axios.get(API_URL_CATEGORIES).then(data => setCategories(data.data))
    }

    return (
        <div className='categories'>
            {categories.map(category =>(
                 <div key={category.id} onClick={() => chooseCategory(category.title)}>{category.title}</div>
            ))}
        </div>
    )
}


export default Categories;
