import React from "react";
import {App} from "../App"

const Category = () => {

    function chooseCategory(category){
        console.log(category)
    }

    return(
        <div className='categories'>
            <div onClick={() => chooseCategory("Все")}>Все</div>
            {App.categories.map(category =>(
                <div key={category.id} onClick={() => chooseCategory(category.title)}>{category.title}</div>
            ))}
        </div>
    )
}

export default Category
