import React, {useEffect, useState} from "react";
import {Container, Table} from "reactstrap";
import axios from "axios";
import './Home.css';


const API_RECIPES = "http://127.0.0.1:8000/api/recipes"
const API_CATEGORIES = "http://127.0.0.1:8000/api/category"

const Home = () => {
    const [recipes, setRecipes] = useState([])
    const [categories, setCategories] = useState([])
    const [Currency, setCurrency] = useState([])

    useEffect(() => {
        axios.get(API_RECIPES)
            .then(rec => {
                setRecipes(rec.data)
                console.log(rec.data)
            })
        axios.get(API_CATEGORIES)
            .then(cat => {
                setCategories(cat.data)
                console.log(cat.data)
            })
    }, []);

    function chooseCategory(category){
        console.log(recipes.map(recipe => recipe.category === category))
    }

    return(
        <Container>
            <Table>
                <thead>
                <tr>
                    <th>Название</th>
                    <th>Текст</th>
                    <th>Категория</th>
                </tr>
                </thead>
                <tbody>
                {recipes.map(recipe => (
                        <tr key={recipe.id}>
                            <td><a href={API_RECIPES}>{recipe.title}</a></td>
                            <td>{recipe.text}</td>
                            <td>{recipe.category}</td>
                        </tr>
                    )
                )}
                {categories.map(cat => (
                        <tr key={cat.id}>
                            <td>{cat.title}</td>
                        </tr>
                    )
                )}
                </tbody>
            </Table>
        </Container>
    )
}




export default Home;
