import {useEffect, useState} from "react";
import {Container, Row, Col, Table} from "reactstrap";
import axios from "axios";
import ListRecipes from "../appListRecipes/ListRecipes";
import {API_URL} from "../../index";


const Home = () => {
    const [recipes, setRecipes] = useState([])

    useEffect(() => {
        getRecipes()
    }, []);

    const getRecipes = (data)=>{
        axios.get(API_URL).then(data => setRecipes(data.data))
    }

    const resetState = () => {
        getRecipes();
    }

    return(
        <Container style={{marginTop: "20px"}}>
            <Row>
                <Col>
                    <ListRecipes recipes={recipes} resetState={resetState} newRecipes={false}/>
                </Col>
            </Row>
            <Table dark>
                <thead>
                <tr>
                    <th>Title</th>
                    <th>Text</th>
                    <th>Category</th>
                </tr>
                </thead>
                <tbody>
                <tr key={recipes.title}>
                    <td>{recipes.title}</td>
                    <td>{recipes.text}</td>
                    <td>{recipes.category}</td>
                </tr>
                </tbody>
            </Table>
        </Container>
    )
}

export default Home;
