import React, {useEffect, useState} from "react";
import {Container, Table} from "reactstrap";
import axios from "axios";
import './Home.css';


const API_URL_RECIPES = "http://127.0.0.1:8000/api/recipes"
const API_URL_CATEGORIES = "http://127.0.0.1:8000/api/category"

const Home = () => {
    const [recipes, setRecipes] = useState([])

    useEffect(() => {
        getRecipes()
    }, []);

    const getRecipes = (data)=>{
        axios.get(API_URL_RECIPES).then(data => setRecipes(data.data))
    }

    const [categories, setCategories] = useState([])

    useEffect(() => {
        getCategories()
    }, []);

    const getCategories = (date) =>{
        axios.get(API_URL_CATEGORIES).then(data => setCategories(data.data))
    }

    function chooseCategory(category){
        console.log(recipes.map(recipe => recipe.category === category))
        return category
    }

    return(
        <Container>
            <div className='categories'>
                {categories.map(category =>(
                    <div key={category.id} onClick={() => chooseCategory(category.title)}>{category.title}</div>
                ))}
            </div>
            <Table>
                <thead>
                <tr>
                    <th>Название</th>
                    <th>Текст</th>
                    <th>Категория</th>
                </tr>
                </thead>
                <tbody>
                {recipes.map(recipe => (
                        <tr key={recipe.id}>
                            <td><a href={API_URL_RECIPES}>{recipe.title}</a></td>
                            <td>{recipe.text}</td>
                            <td>{recipe.category}</td>
                        </tr>
                    )
                )}
                </tbody>
            </Table>
        </Container>
    )
}




export default Home;
