import './App.css';
import React, {useEffect, useState} from "react";
import Recipes from './/components/Recipes'
import axios from "axios";
import Category from "./components/Category";


const API_RECIPES = "http://127.0.0.1:8000/api/recipes"
const API_CATEGORIES = "http://127.0.0.1:8000/api/category"

function App(){
    const [recipes, setRecipes] = useState([])
    const [categories, setCategories] = useState([])
    const [categoryId, setCategoryId] = useState("Супы")

    useEffect(() => {
        axios.get(API_RECIPES)
            .then(rec => {
                setRecipes(rec.data)
            })
        axios.get(API_CATEGORIES)
            .then(cat => {
                setCategories(cat.data)
            })
    }, []);



    return (
        <div className='App'>
            <div className="text-center">
                <div>
                    <h1 className='logo'>Рецепты</h1>
                    <ul className='nav'>
                    </ul>
                </div>
            </div>
            <Category />
            {recipes.map(el => (
                <Recipes key={el.id}
                    title={el.title}
                    text={el.text}
                />
            ))}
        </div>
    );
}


export default App;
