import {useEffect, useState} from "react";
import {Container, Row, Col} from "reactstrap";
import axios from "axios";
import ListRecipes from "../../../../recipes/backend_api/models.py";
import {API_URL} from "../../../../";


const Home = () => {
    const [recipes, setRecipes] = useState([])

    useEffect(() => {
        getRecipes()
    }, []);

    const getRecipes = (data)=>{
        axios.get(API_URL).then(data => setRecipes(data.data))
    }

    const resetState = () => {
        getRecipes();
    }

    return(
        <Container style={{marginTop: "20px"}}>
            <Row>
                <Col>
                    <ListRecipes students={recipes} resetState={resetState} newRecipes={false}/>
                </Col>
            </Row>
        </Container>
    )
}

export default Home;
