import {useEffect, useState} from "react";
import {Container, Table} from "reactstrap";
import axios from "axios";
import {API_URL} from "../../index";


const Home = () => {
    const [recipes, setRecipes] = useState([])

    useEffect(() => {
        getRecipes()
    }, []);

    const getRecipes = (data)=>{
        axios.get(API_URL).then(data => setRecipes(data.data))
    }

    return(
            <Table dark>
                <thead>
                <tr>
                    <th>Title</th>
                    <th>Text</th>
                    <th>Category</th>
                </tr>
                </thead>
                <tbody>
                {recipes.map(recipe => (
                        <tr key={recipe.pk}>
                            <td>{recipe.title}</td>
                            <td>{recipe.text}</td>
                            <td>{recipe.category}</td>
                        </tr>
                    )
                )}
                </tbody>
            </Table>
    )
}

export default Home;
