import './App.css';
import axios from "axios";
import React from "react";


class App extends React.Component{
    state = {detail: [], }

    componentDidMount() {
        let data;
        axios.get("http://127.0.0.1:8000/")
            .then(res => {
                data = res.data;
                this.setState({
                    detail: data
                });
            })
            .catch(err =>{
                console.log(err);
            })
    }
    render(){
        return (
            <div>
                <header><h1>Рецепты</h1></header>
                <hr></hr>
                {this.state.detail.map((output, id) =>(
                    <div key={id}>
                        <div>
                            <h2>{output.title}</h2>
                            <p>{output.text}</p>
                            <p>{output.category}</p>
                        </div>
                    </div>
                ))}
            </div>
        )
    }
}

export default App;
