import './App.css';
import React, {useEffect, useState} from "react";
import Recipes from './/components/Recipes'
import axios from "axios";


const API_RECIPES = "http://127.0.0.1:8000/api/recipes"
const API_CATEGORIES = "http://127.0.0.1:8000/api/category"

function App(){
    const [recipes, setRecipes] = useState([])
    const [categories, setCategories] = useState([])

    useEffect(() => {
        axios.get(API_RECIPES)
            .then(rec => {
                setRecipes(rec.data)
                console.log(rec.data)
            })
        axios.get(API_CATEGORIES)
            .then(cat => {
                setCategories(cat.data)
                console.log(cat.data)
            })
    }, []);

    return (
        <div className='App'>
            <div className='categories'>
                {categories.map(category =>(
                    <div key={category.id} onClick={() => chooseCategory(category.title)}>{category.title}</div>
                ))}
            </div>
            <div className="text-center">
                <div>
                    <h1 className='logo'>Рецепты</h1>
                    <ul className='nav'>
                    </ul>
                </div>
            </div>
            <Recipes
                title='Вареник'
                text='Кидай в воду и всё'
                category='второе'
            />
        </div>
    );
}





export default App;
